drop database if exists `example_auth`;
create database `example_auth`;
use `example_auth`;

create table `users` (
  `id` int not null auto_increment,
  `username` varchar(100) not null,
  `email` varchar(100) not null,
  `password_hash` varchar(64) not null,
  primary key (`id`),
  unique (`username`),
  unique (`email`)
);

